INSERT INTO content (title, content_type, description, release_date, genre, age_rating, language, duration, director, "cast", streaming_info, trailer_url, poster_url, rating_avg) VALUES
('Baahubali: The Beginning', 'movie', 'In the ancient kingdom of Mahishmati, a daring young man named Shivudu uncovers his royal legacy as the son of the legendary warrior Amarendra Baahubali. As he learns about his father''s heroic deeds and tragic betrayal by his own brother Bhallaladeva, Shivudu must reclaim his rightful throne in this epic tale of courage, love, and betrayal that features breathtaking action sequences and stunning visual effects.', '2015-07-10', ARRAY['Action', 'Adventure', 'Drama'], 'PG-13', 'Telugu', 159, 'S. S. Rajamouli', ARRAY['Prabhas', 'Rana Daggubati', 'Anushka Shetty', 'Tamannaah', 'Ramya Krishnan'], ARRAY['Netflix', 'Amazon Prime'], 'https://youtu.be/sOEg_YZQsTI', 'https://i.pinimg.com/736x/36/44/3d/36443dc099d96078744a1d4de9863787.jpg', NULL),
('Baahubali 2: The Conclusion', 'movie', 'The dramatic conclusion to the Baahubali saga reveals why Kattappa killed Amarendra Baahubali while following Mahendra Baahubali''s quest to avenge his father''s death and reclaim the Mahishmati kingdom from the tyrannical rule of Bhallaladeva. This visually spectacular finale features even grander battles, emotional reunions, and answers to all the mysteries set up in the first film.', '2017-04-28', ARRAY['Action', 'Fantasy'], 'PG-13', 'Telugu', 171, 'S. S. Rajamouli', ARRAY['Prabhas', 'Anushka Shetty', 'Rana Daggubati', 'Tamannaah', 'Ramya Krishnan'], ARRAY['Netflix'], 'https://youtu.be/G62HrubdD6o', 'https://www.themoviedb.org/t/p/w1280/xQ22LOWSkClP3maYhR9nZH0dnWM.jpg', NULL),
('Pushpa: The Rise', 'movie', 'Pushpa Raj, a humble lorry driver working for red sandalwood smugglers in the Seshachalam forests, rises through the ranks of the illegal smuggling syndicate through his intelligence, ruthlessness, and ambition. As he challenges the existing power structures and makes powerful enemies, Pushpa must navigate treacherous alliances in this gritty action drama featuring Allu Arjun''s iconic performance and memorable dialogues.', '2021-12-17', ARRAY['Action', 'Drama'], 'PG-13', 'Telugu', 179, 'Sukumar', ARRAY['Allu Arjun', 'Rashmika Mandanna', 'Fahadh Faasil'], ARRAY['Amazon Prime'], 'https://youtu.be/Q1NKMPhP8PY', 'https://www.themoviedb.org/t/p/w1280/r1yAzVQNbCbPTbB3GZFour9Qr0t.jpg', NULL),
('Arjun Reddy', 'movie', 'Arjun Reddy, a brilliant but self-destructive surgeon, spirals into alcoholism and drug abuse when his girlfriend Preeti is forced to marry someone else. This raw and intense romantic drama explores toxic masculinity, obsessive love, and redemption as Arjun hits rock bottom before attempting to rebuild his life. The film sparked controversy for its portrayal of relationships but gained cult status for its uncompromising vision.', '2017-08-25', ARRAY['Drama', 'Romance'], 'A', 'Telugu', 182, 'Sandeep Reddy Vanga', ARRAY['Vijay Deverakonda', 'Shalini Pandey'], ARRAY['Aha', 'Amazon Prime'], 'https://youtu.be/aozErFqz3gU', 'https://www.themoviedb.org/t/p/w1280/u9yryEQRGJYobvtneoyeIEhuCUj.jpg', NULL),
('Eega', 'movie', 'After being murdered by a jealous industrialist named Sudeep, Nani is reincarnated as a housefly with retained human memories. The tiny but determined fly wages an inventive and often hilarious one-insect war against his killer to protect the woman he loves. This innovative fantasy film blends live-action with groundbreaking CGI to tell a unique revenge story from a fly''s-eye view.', '2012-07-06', ARRAY['Fantasy', 'Action'], 'U', 'Telugu', 134, 'S. S. Rajamouli', ARRAY['Nani', 'Samantha Ruth Prabhu', 'Sudeep'], ARRAY['Disney+ Hotstar'], 'https://youtu.be/6jVwFf4bH74', 'https://www.themoviedb.org/t/p/w1280/xbCPJejbJgRIuOwUzJqcBUjCbFB.jpg', NULL),
('Mahanati', 'movie', 'This biographical drama chronicles the rise and fall of Savitri, the legendary South Indian actress who ruled the silver screen in the 1950s-60s. From her humble beginnings to becoming the first female superstar of Indian cinema, and her subsequent decline due to personal struggles, the film paints a poignant portrait of fame, love, and tragedy through exquisite period detail and Keerthy Suresh''s award-winning performance.', '2018-05-09', ARRAY['Biography', 'Drama'], 'PG', 'Telugu', 177, 'Nag Ashwin', ARRAY['Keerthy Suresh', 'Dulquer Salmaan', 'Samantha Ruth Prabhu'], ARRAY['Amazon Prime'], 'https://youtu.be/A0Gx78bC-2Q', 'https://www.themoviedb.org/t/p/w1280/5hwtlwoLdSpkoeusT0sf8qW5VFB.jpg', NULL),
('Ala Vaikunthapurramuloo', 'movie', 'Bantu grows up neglected by his father who believes another man''s son is his own due to a hospital mix-up. When Bantu discovers his true parentage and that he''s actually the heir to a wealthy family, he must navigate the complexities of his new position while dealing with family politics, romance, and his doppelg√§nger. This stylish masala entertainer features Allu Arjun''s electrifying dance moves and a chart-topping soundtrack.', '2020-01-12', ARRAY['Action', 'Comedy', 'Drama'], 'PG', 'Telugu', 165, 'Trivikram Srinivas', ARRAY['Allu Arjun', 'Pooja Hegde', 'Tabu', 'Jayaram'], ARRAY['Netflix'], 'https://youtu.be/QdD3Qd-lLfY', 'https://www.themoviedb.org/t/p/w1280/2rzORJaegE2bbKNVkQXbZCeV0BP.jpg', NULL),
('RRR', 'movie', 'This fictional tale imagines a friendship between two real-life Indian revolutionaries - Alluri Sitarama Raju and Komaram Bheem - during the British Raj. When a British officer kidnaps a tribal girl, Bheem comes to Delhi to rescue her and forms an unlikely bond with Raju, unaware that Raju is working undercover for the British. The film''s spectacular action sequences, emotional depth, and technical brilliance made it a global phenomenon.', '2022-03-25', ARRAY['Action', 'Historical', 'Drama'], 'PG-13', 'Telugu', 182, 'S. S. Rajamouli', ARRAY['N. T. Rama Rao Jr.', 'Ram Charan', 'Alia Bhatt', 'Ajay Devgn'], ARRAY['Netflix'], 'https://youtu.be/f_vbAtFSEc0', 'https://www.themoviedb.org/t/p/w1280/nEufeZlyAOLqO2brrs0yeF1lgXO.jpg', NULL),
('Sita Ramam', 'movie', 'In 1985, an orphaned woman receives letters sent to her by an army officer named Ram in 1964. Through these letters, she uncovers a poignant love story between Lieutenant Ram, stationed in Kashmir, and Sita Mahalakshmi, the princess of a royal family. As their romance blossoms across religious and social divides, political tensions rise, leading to a heartbreaking separation with consequences that echo decades later.', '2022-08-05', ARRAY['Romance', 'Drama'], 'U', 'Telugu', 163, 'Hanu Raghavapudi', ARRAY['Dulquer Salmaan', 'Mrunal Thakur'], ARRAY['Amazon Prime'], 'https://youtu.be/3bomQxDkOaQ', 'https://www.themoviedb.org/t/p/w1280/zcsKp0GvD2qlgn5zOuENJ0lT9UK.jpg', NULL),
('Jersey', 'movie', 'Arjun, a talented but failed cricketer in his late 30s, decides to make a comeback to fulfill his son''s wish for a cricket jersey. As he battles age, financial struggles, and skepticism from everyone around him, Arjun''s journey becomes an emotional testament to second chances and the power of dreams. Nani delivers a career-best performance in this sports drama that celebrates perseverance against all odds.', '2019-04-19', ARRAY['Drama', 'Sport'], 'U', 'Telugu', 157, 'Gowtam Tinnanuri', ARRAY['Nani', 'Shraddha Srinath'], ARRAY['Zee5'], 'https://youtu.be/NaZHM9bxpuI', 'https://www.themoviedb.org/t/p/w1280/bU9q9yVtxeBiC0Do27CekHXNE6D.jpg', NULL),
('3 Idiots', 'movie', 'Two friends embark on a journey to find their long-lost college companion Ranchoddas. Through flashbacks, they recall their days at an elite engineering college where they rebelled against the oppressive system under the guidance of the free-spirited Rancho, whose unconventional wisdom changed their lives. This heartwarming comedy-drama critiques educational pressures while celebrating friendship and pursuing one''s passion.', '2009-12-25', ARRAY['Comedy', 'Drama'], 'PG-13', 'Hindi', 170, 'Rajkumar Hirani', ARRAY['Aamir Khan', 'R. Madhavan', 'Sharman Joshi', 'Kareena Kapoor'], ARRAY['Netflix', 'Amazon Prime'], 'https://youtu.be/K0eDlFX9GMc', 'https://www.themoviedb.org/t/p/w1280/66A9MqXOyVFCssoloscw79z8Tew.jpg', NULL),
('Lagaan', 'movie', 'In 1893, during the British Raj, villagers from Champaner face crippling taxes during a drought. When arrogant officer Captain Russell offers them a impossible wager - beat the British at cricket to waive their taxes for three years - the villagers, led by Bhuvan, must learn the alien sport to save their homes. This epic sports drama blends historical fiction with rousing underdog triumph in a vibrant musical package.', '2001-06-15', ARRAY['Drama', 'Sport'], 'PG', 'Hindi', 224, 'Ashutosh Gowariker', ARRAY['Aamir Khan', 'Gracy Singh', 'Rachel Shelley'], ARRAY['Netflix'], 'https://youtu.be/1dCC0UuUhJk', 'https://www.themoviedb.org/t/p/w1280/yNX9lFRAFeNLNRIXdqZK9gYrYKa.jpg', NULL),
('Swades', 'movie', 'NASA scientist Mohan Bhargava returns to India to find his childhood nanny and is gradually drawn into the struggles of rural life. Witnessing the poverty, caste discrimination, and lack of basic amenities, Mohan undergoes a transformation as he uses his technical skills to bring electricity to a village, discovering his true calling in the process. This thoughtful drama explores patriotism beyond nationalism.', '2004-12-17', ARRAY['Drama'], 'PG', 'Hindi', 210, 'Ashutosh Gowariker', ARRAY['Shah Rukh Khan', 'Gayatri Joshi'], ARRAY['Netflix'], 'https://youtu.be/nkS_Ar0Yad0', 'https://www.themoviedb.org/t/p/w1280/yUSL24kpHc9Nls4Pohia4shgcIM.jpg', NULL),
('Gully Boy', 'movie', 'Inspired by the lives of Mumbai street rappers like Divine and Naezy, this film follows Murad, a young man from the Dharavi slums who channels his frustrations and dreams into rap music. As he navigates family pressures, class struggles, and the underground hip-hop scene, Murad finds his voice and a path to self-expression in this vibrant musical drama that launched India''s rap revolution.', '2019-02-14', ARRAY['Musical', 'Drama'], 'PG-13', 'Hindi', 154, 'Zoya Akhtar', ARRAY['Ranveer Singh', 'Alia Bhatt', 'Siddhant Chaturvedi'], ARRAY['Amazon Prime'], 'https://youtu.be/JfbxcD6biOk', 'https://www.themoviedb.org/t/p/w1280/4RE7TD5TqEXbPKyUHcn7CSeMlrJ.jpg', NULL),
('Kahaani', 'movie', 'Vidya Bagchi, a pregnant woman from London, arrives in Kolkata searching for her missing husband. As she digs deeper into the disappearance with the help of a local cop, she uncovers a web of deception connected to a deadly terrorist attack. This gripping thriller keeps audiences guessing until its shocking climax, with Vidya Balan delivering a powerhouse performance as the determined protagonist.', '2012-03-09', ARRAY['Thriller', 'Mystery'], 'PG-13', 'Hindi', 122, 'Sujoy Ghosh', ARRAY['Vidya Balan', 'Parambrata Chatterjee', 'Nawazuddin Siddiqui'], ARRAY['Netflix'], 'https://youtu.be/j4sIlgcH0-U', 'https://www.themoviedb.org/t/p/w1280/e2eQVOrdQ8k7yYjjHKHP2nlwbTu.jpg', NULL),
('Barfi!', 'movie', 'Murphy "Barfi" Johnson, a charming deaf-mute man in 1970s Darjeeling, forms a deep bond with Shruti, who ultimately chooses conventional security over their unconventional love. Years later, when Barfi reconnects with his autistic childhood friend Jhilmil, their innocent relationship blossoms into something beautiful. This whimsical romance celebrates life''s simple joys and the universal language of love beyond words.', '2012-09-14', ARRAY['Comedy', 'Drama', 'Romance'], 'PG', 'Hindi', 151, 'Anurag Basu', ARRAY['Ranbir Kapoor', 'Priyanka Chopra', 'Ileana DCruz'], ARRAY['Netflix'], 'https://youtu.be/yZxrao3zou4', 'https://www.themoviedb.org/t/p/w1280/3dMcxaL0oD79nc31FQK5D7YZ3Kv.jpg', NULL),
('Queen', 'movie', 'When her fianc√© calls off their wedding just days before the ceremony, Rani, a sheltered Delhi girl, decides to go on her planned honeymoon alone. From Paris to Amsterdam, her solo journey becomes a liberating adventure of self-discovery as she befriends fellow travelers, confronts her fears, and learns to embrace life on her own terms in this heartwarming feminist comedy-drama.', '2014-03-07', ARRAY['Adventure', 'Comedy', 'Drama'], 'PG', 'Hindi', 146, 'Vikas Bahl', ARRAY['Kangana Ranaut', 'Rajkummar Rao', 'Lisa Haydon'], ARRAY['Netflix'], 'https://youtu.be/KGC6vl3lzf0', 'https://www.themoviedb.org/t/p/w1280/vKLp0X2RQOuA31R3AdaYtDdKIPK.jpg', NULL),
('Dangal', 'movie', 'Based on the true story of Mahavir Singh Phogat, a former wrestler who trains his daughters Geeta and Babita to become India''s first world-class female wrestlers. Breaking gender stereotypes in rural Haryana, Mahavir''s rigorous coaching and belief in his daughters'' potential leads them to Commonwealth Games glory in a sports drama that combines rousing action with emotional family dynamics.', '2016-12-23', ARRAY['Action', 'Biography', 'Drama'], 'PG', 'Hindi', 161, 'Nitesh Tiwari', ARRAY['Aamir Khan', 'Fatima Sana Shaikh', 'Sanya Malhotra'], ARRAY['Netflix'], 'https://youtu.be/x_7YlGv9u1g', 'https://www.themoviedb.org/t/p/w1280/lHd3W8E5aKoki9pDP7tN7yEh3c0.jpg', NULL),
('Piku', 'movie', 'Piku Banerjee, a headstrong architect, lives with her hypochondriac father Bhaskor whose eccentricities dominate their lives. When they embark on a road trip from Delhi to Kolkata to visit their ancestral home, accompanied by pragmatic taxi owner Rana, the journey becomes a hilarious and heartwarming exploration of family bonds, generational differences, and letting go.', '2015-05-08', ARRAY['Comedy', 'Drama'], 'PG', 'Hindi', 123, 'Shoojit Sircar', ARRAY['Deepika Padukone', 'Amitabh Bachchan', 'Irrfan Khan'], ARRAY['Amazon Prime'], 'https://youtu.be/oeiKUlUUNQ8', 'https://www.themoviedb.org/t/p/w1280/yo7EZWiLpLGhqs3YnlDGDBPjfTb.jpg', NULL),
('Andhadhun', 'movie', 'Akash, a blind pianist pretending to be visually impaired for artistic inspiration, unwittingly becomes embroiled in the murder of a former film star. As he navigates a web of lies, blackmail, and coincidences, the line between his performance and reality blurs in this darkly comic thriller filled with shocking twists and morally ambiguous characters.', '2018-10-05', ARRAY['Crime', 'Thriller'], 'PG-13', 'Hindi', 139, 'Sriram Raghavan', ARRAY['Ayushmann Khurrana', 'Tabu', 'Radhika Apte'], ARRAY['Netflix'], 'https://youtu.be/2iVYI99VGaw', 'https://www.themoviedb.org/t/p/w1280/dy3K6hNvwE05siGgiLJcEiwgpdO.jpg', NULL),
('Article 15', 'movie', 'IPS officer Ayan Ranjan arrives in rural Uttar Pradesh to investigate the disappearance of three Dalit girls, uncovering deep-rooted caste discrimination and systemic apathy. As he confronts the brutal realities of India''s social hierarchy and police complicity, Ayan must navigate moral dilemmas and bureaucratic hurdles to deliver justice in this hard-hitting political thriller inspired by real incidents.', '2019-06-28', ARRAY['Crime', 'Drama'], 'PG-13', 'Hindi', 130, 'Anubhav Sinha', ARRAY['Ayushmann Khurrana', 'Isha Talwar', 'Manoj Pahwa'], ARRAY['Netflix'], 'https://youtu.be/HKOJY0cMeUQ', 'https://www.themoviedb.org/t/p/w1280/7pCaexxVAq37Q9Rzo3saBP8DOHn.jpg', NULL),
('Drishyam', 'movie', 'Vijay Salgaonkar, a cable operator with a fourth-grade education, becomes embroiled in a high-stakes cat-and-mouse game when his family accidentally kills the son of Inspector General Meera Deshmukh. Using his wits and knowledge gleaned from crime films, Vijay constructs an elaborate alibi in this gripping thriller about how far a father will go to protect his family.', '2015-07-31', ARRAY['Crime', 'Drama', 'Mystery'], 'PG-13', 'Hindi', 163, 'Nishikant Kamat', ARRAY['Ajay Devgn', 'Shriya Saran', 'Tabu'], ARRAY['Amazon Prime'], 'https://youtu.be/AuuX2j14NBg', 'https://www.themoviedb.org/t/p/w1280/gIClWRv5OSe8rl5Koi0AeUcCZ9Z.jpg', NULL),
('Pink', 'movie', 'When three independent women are falsely accused of assaulting a powerful politician''s nephew after a party gone wrong, retired lawyer Deepak Sehgal takes up their case pro bono. This courtroom drama exposes societal prejudices against women''s freedom and consent, delivering a powerful message about equality and the importance of "no means no" in contemporary India.', '2016-09-16', ARRAY['Drama', 'Thriller'], 'PG-13', 'Hindi', 136, 'Aniruddha Roy Chowdhury', ARRAY['Amitabh Bachchan', 'Taapsee Pannu', 'Kirti Kulhari'], ARRAY['Netflix'], 'https://youtu.be/AL2TShb6fFs', 'https://www.themoviedb.org/t/p/w1280/6xNhnyKm2M5FEOY7xv5iKiQ5P3.jpg', NULL);
INSERT INTO content (title, content_type, description, release_date, genre, age_rating, language, duration, director, "cast", streaming_info, trailer_url, poster_url, rating_avg) VALUES
('Bhale Bhale Magadivoy', 'movie', 'A forgetful scientist with short-term memory loss falls in love but struggles to maintain his relationship due to his condition. This hilarious romantic comedy showcases Nani''s impeccable comic timing as he navigates increasingly absurd situations caused by his memory lapses, culminating in a heartwarming resolution about love overcoming all obstacles.', '2015-09-04', ARRAY['Comedy', 'Romance'], 'U', 'Telugu', 153, 'Maruthi', ARRAY['Nani', 'Lavanya Tripathi'], ARRAY['Amazon Prime'], 'https://youtu.be/2HmGdZ3gF4s', 'https://www.themoviedb.org/t/p/w1280/fa2Nv68gcmgZJbKCJV5T45fwA8P.jpg', NULL),
('Ninnu Kori', 'movie', 'Uma, settled in the US, returns to India to win back his ex-girlfriend Pallavi who is about to marry another man. This emotional romantic drama explores themes of love, sacrifice, and moving on, with Nani delivering a nuanced performance as a man torn between his feelings and doing what''s right for the woman he loves.', '2017-07-07', ARRAY['Drama', 'Romance'], 'U', 'Telugu', 142, 'Shiva Nirvana', ARRAY['Nani', 'Nivetha Thomas', 'Aadhi Pinisetty'], ARRAY['Disney+ Hotstar'], 'https://youtu.be/5I1y5K8XJ5Y', 'https://www.themoviedb.org/t/p/w1280/tSqphL6d7slQ0zFmnhliYE9oyAS.jpg', NULL),
('Gentleman', 'movie', 'A vigilante journalist adopts two identities - a suave businessman and a rugged common man - to expose corruption in the education system. This action thriller showcases Nani''s versatility as he plays dual roles in a cat-and-mouse game with a powerful politician, blending social message with commercial entertainment.', '2016-06-17', ARRAY['Action', 'Thriller'], 'UA', 'Telugu', 147, 'Mohan Krishna Indraganti', ARRAY['Nani', 'Surbhi'], ARRAY['Amazon Prime'], 'https://youtu.be/5I1y5K8XJ5Y', 'https://www.themoviedb.org/t/p/w1280/hoO5BunFOXp5s8WkuRB4SgsruaS.jpg', NULL),
('Shyam Singha Roy', 'movie', 'A young filmmaker begins experiencing visions of a past life as Shyam Singha Roy, a revolutionary poet who fought against social injustice in 1970s Bengal. As he investigates these memories, he uncovers a tragic love story and unfinished mission that he must complete in this reincarnation drama blending period and contemporary narratives.', '2021-12-24', ARRAY['Drama', 'Romance'], 'UA', 'Telugu', 155, 'Rahul Sankrityan', ARRAY['Nani', 'Sai Pallavi', 'Krithi Shetty'], ARRAY['Netflix'], 'https://youtu.be/9Q1Iq9JQYqY', 'https://www.themoviedb.org/t/p/w1280/nUHk72rSGDuG2Lz22zJAYCyxq7g.jpg', NULL),
('Dasara', 'movie', 'Set in a rustic village dominated by liquor and violence, three childhood friends find their bond tested by circumstances beyond their control. This raw and intense drama explores themes of friendship, betrayal, and redemption against the backdrop of rural Telangana''s political and social realities.', '2023-03-30', ARRAY['Action', 'Drama'], 'UA', 'Telugu', 158, 'Srikanth Odela', ARRAY['Nani', 'Keerthy Suresh'], ARRAY['Netflix'], 'https://youtu.be/9Q1Iq9JQYqY', 'https://www.themoviedb.org/t/p/w1280/4OTVoCVXa5aHDZl8opKoIV9Ezr5.jpg', NULL);
INSERT INTO content (title, content_type, description, release_date, genre, age_rating, language, duration, director, "cast", streaming_info, trailer_url, poster_url, rating_avg) VALUES
('Magadheera', 'movie', 'In a tale spanning 400 years, a warrior reincarnates to protect his lover from their past life''s nemesis. This epic fantasy romance established Ram Charan as a leading star, featuring breathtaking action sequences, lavish production design, and a timeless love story that transcends centuries.', '2009-07-31', ARRAY['Action', 'Fantasy', 'Romance'], 'U', 'Telugu', 166, 'S. S. Rajamouli', ARRAY['Ram Charan', 'Kajal Aggarwal'], ARRAY['Disney+ Hotstar'], 'https://youtu.be/aqz-KE-bpKQ', 'https://www.themoviedb.org/t/p/w1280/w9zOKnYXLZOccjn4vjd3EkuVFKX.jpg', NULL),
('Rangasthalam', 'movie', 'Chitti Babu, a partially deaf villager, gets embroiled in his brother''s crusade against a corrupt village president in 1980s Andhra Pradesh. This rustic drama with political undertones features Ram Charan''s career-best performance and authentic period detailing that transports viewers to rural India during economic transition.', '2018-03-30', ARRAY['Drama', 'Action'], 'UA', 'Telugu', 170, 'Sukumar', ARRAY['Ram Charan', 'Samantha Ruth Prabhu'], ARRAY['Disney+ Hotstar'], 'https://youtu.be/9Q1Iq9JQYqY', 'https://www.themoviedb.org/t/p/w1280/yiEzDgBBFC25Zd6z0r7sMngn5vr.jpg', NULL),
('Dhruva', 'movie', 'An upright IPS officer goes undercover to dismantle a powerful criminal syndicate led by a ruthless businessman. This gripping cat-and-mouse thriller showcases Ram Charan in a intense performance as he methodically brings down a seemingly untouchable villain in a battle of wits and willpower.', '2016-12-09', ARRAY['Action', 'Thriller'], 'UA', 'Telugu', 158, 'Surender Reddy', ARRAY['Ram Charan', 'Rakul Preet Singh'], ARRAY['Disney+ Hotstar'], 'https://youtu.be/9Q1Iq9JQYqY', 'https://www.themoviedb.org/t/p/w1280/hh7D1fLjet1Ce1kcCkk5TVr3g9O.jpg', NULL),
('Vinaya Vidheya Rama', 'movie', 'Rama, a protective brother, takes on a powerful politician when his younger brother gets entangled in criminal activities. This action-packed family drama features Ram Charan in a mass avatar with high-octane fight sequences and emotional family moments.', '2019-01-11', ARRAY['Action', 'Drama'], 'UA', 'Telugu', 150, 'Boyapati Srinu', ARRAY['Ram Charan', 'Kiara Advani'], ARRAY['Disney+ Hotstar'], 'https://youtu.be/9Q1Iq9JQYqY', 'https://www.themoviedb.org/t/p/w1280/tOjSnrKwI3qVKMHn5ofwc2K3Sm3.jpg', NULL),
('Yevadu', 'movie', 'A man with a surgically altered face seeks revenge against those who killed his lover while protecting a woman who shares her likeness. This action thriller explores themes of identity, vengeance, and redemption with Ram Charan playing dual roles in a complex narrative of switched faces and hidden agendas.', '2014-01-12', ARRAY['Action', 'Thriller'], 'UA', 'Telugu', 162, 'Vamsi Paidipally', ARRAY['Ram Charan', 'Allu Arjun', 'Shruti Haasan'], ARRAY['Disney+ Hotstar'], 'https://youtu.be/9Q1Iq9JQYqY', 'https://www.themoviedb.org/t/p/w1280/euSAn9sVeS4Qnaj1ZWDw56gPi9W.jpg', NULL);
